/* eslint-disable */

// IIFE 添加按钮各类事件
(function(document) {

  var HD_DOMUtil = {
    initClickEvent: function initClickEvent(dom, callback) {
      if (document.addEventListener) { // 一般浏览器
        dom.addEventListener('click', callback, false);
      } else if (document.attachEvent) { // IE
        dom.attachEvent('onclick', callback);
      } else {
        dom.onclick = callback;
      }
    },
    addClass: function addClass(dom, className) {
      dom.className = dom.className + ' ' + className;
    },

    removeClass: function removeClass(dom, className) {
      var oldClass = dom.className;
      dom.className = oldClass.replace(' ' + className, '');
      dom.className = oldClass.replace(className, '');
    },

    hasClass: function hasClass(dom, className) {
      return dom.className.match(' ' + className) || dom.className.match(className);
    },

    toggleClass: function toggleClass(dom, className) {
      if (this.hasClass(dom, className)) {
        this.removeClass(dom, className);
      } else {
        this.addClass(dom, className);
      }
    }
  };

  // 移动端导航栏显隐事件
  var toggleButton = document.getElementById('toggleNav');
  var nav = document.getElementById('nav');
  var isMobile = document.documentElement.clientWidth <= 768;
  var mobileNavShow = false;

  HD_DOMUtil.initClickEvent(toggleButton, function() {
    if (mobileNavShow) {
      HD_DOMUtil.addClass(toggleButton, 'active');
      nav.style.display = 'none';
      mobileNavShow = false;
    } else {
      HD_DOMUtil.removeClass(toggleButton, 'active');
      nav.style.display = 'block';
      mobileNavShow = true;
    }
  });

  // 导航栏事件
  var toHome = document.getElementById('toHome');
  var home = document.getElementById('home');

  var toExplore = document.getElementById('toExplore');
  var explore = document.getElementById('explore');

  HD_DOMUtil.initClickEvent(toHome, function() {
    HD_DOMUtil.toggleClass(toHome, 'active');
    home.style.display = 'block';
    HD_DOMUtil.toggleClass(toExplore, 'active');
    explore.style.display = 'none';
    if (isMobile) {
      HD_DOMUtil.removeClass(toggleButton, 'active');
      nav.style.display = 'none';
      mobileNavShow = false;
    }
  });

  HD_DOMUtil.initClickEvent(toExplore, function() {
    HD_DOMUtil.toggleClass(toHome, 'active');
    home.style.display = 'none';
    HD_DOMUtil.toggleClass(toExplore, 'active');
    explore.style.display = 'block';
    if (isMobile) {
      HD_DOMUtil.removeClass(toggleButton, 'active');
      nav.style.display = 'none';
      mobileNavShow = false;
    }
  });

  // 信息披露选择事件
  var agencyInfoLink = document.getElementById('toAgencyInfo');
  var businessInfoLink = document.getElementById('toBusinessInfo');
  var agencyInfo = document.getElementById('agencyInfo');
  var businessInfo = document.getElementById('businessInfo');

  HD_DOMUtil.initClickEvent(agencyInfoLink, function() {
    HD_DOMUtil.toggleClass(agencyInfoLink, 'active');
    agencyInfo.style.display = 'block';
    HD_DOMUtil.toggleClass(businessInfoLink, 'active');
    businessInfo.style.display = 'none';
  });

  HD_DOMUtil.initClickEvent(businessInfoLink, function() {
    HD_DOMUtil.toggleClass(agencyInfoLink, 'active');
    agencyInfo.style.display = 'none';
    HD_DOMUtil.toggleClass(businessInfoLink, 'active');
    businessInfo.style.display = 'block';
  });
}(document));
